import React from 'react'
import Calendar from './Calendar'

const Sales = () => {

  let dateNow = new Date();
  const month = dateNow.getMonth();
  const dayToday = dateNow.getDate();

  let monthString;
  let daysNumber;

  switch (month) {
    case 0:
      monthString = "January";
      daysNumber = 31;
      break;
    case 1:
      monthString = "Febuary";
      daysNumber = 28;
      break;
    case 2:
      monthString = "March";
      daysNumber = 31;
      break;
    case 3:
      monthString = "April";
      daysNumber = 30;
      break;
    case 4:
      monthString = "May";
      daysNumber = 31;
      break;
    case 5:
      monthString = "June";
      daysNumber = 30;
      break;
    case 6:
      monthString = "July";
      daysNumber = 31;
      break;
    case 7:
      monthString = "August";
      daysNumber = 31;
      break;
    case 8:
      monthString = "September";
      daysNumber = 30;
      break;
    case 9:
      monthString = "October";
      daysNumber = 31;
      break;
    case 10:
      monthString = "November";
      daysNumber = 30;
      break;
    case 11:
      monthString = "December";
      daysNumber = 31;
      break;
    default:
      break;
  }

  return (
    <div className='sales'>
      <div className='charts'></div>
      <Calendar month={monthString} days={daysNumber} dayToday={dayToday} />
      <div className='sale'>
        <h3>المبيعات</h3>
        <ul>
          <li>
            <span>اجمالي المبيعات</span>
            <span>50</span>
          </li>
          <li>
            <span>تكاليف المنتجات</span>
            <span>50</span>
          </li>
          <li>
            <span>الشحن</span>
            <span>50</span>
          </li>
          <li>
            <span>رسوم الدفع الالكتروني</span>
            <span>50</span>
          </li>
        </ul>
        <div>
          <span>صافي المبيعات</span>
          <span>50</span>
        </div>
      </div>
    </div>
  )
}

export default Sales
