import React, { useState } from 'react'

const Form = ({ submitValue }) => {

  const [inputVal, getInputVal] = useState({
    name: "",
    email: "",
    phone: "",
    password: "",
  });

  const onChangeHandler = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    getInputVal((prevState) => {
      return { ...prevState, [name]: value };
    })
  }

  const nextHandler = (e) => {
    e.preventDefault();
    console.log(inputVal.name);
    console.log(inputVal.email);
    console.log(inputVal.phone);
    console.log(inputVal.password);
  };

  return (
    <form className='form-shop' onSubmit={nextHandler}>
      <label>الاسم كاملا</label>
      <input
        type="text"
        placeholder="ادخل الاسم كاملاً.."
        name="name"
        value={inputVal.name}
        onChange={onChangeHandler}
        required />
      <label>البريد الإلكتروني</label>
      <input
        type="email"
        placeholder="ادخل  البريد الإلكتروني.."
        name="email"
        value={inputVal.email}
        onChange={onChangeHandler}
        required />
      <label>رقم الهاتف</label>
      <input
        type="phone"
        placeholder="ادخل رقم الهاتف.."
        name="phone"
        value={inputVal.phone}
        onChange={onChangeHandler}
        required />
      <label>كلمة المرور</label>
      <input
        type="password"
        placeholder="كلمة المرور.."
        name="password"
        value={inputVal.password}
        onChange={onChangeHandler}
        required />
      <input type='submit' value={submitValue} />
    </form>
  )
}

export default Form
