import React from "react";
import { useState } from "react";
import TableProduct from "./TableProduct";
import ProductBox from "./ProductBox";

import "./products.css";

const Products = () => {
  const [displayFormat, getDisplayFormat] = useState("bar");
  const [colorFormat, getColorFormat] = useState(true);

  const itemsProducts = [
    {
      id: 1,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "منشور",
    },
    {
      id: 2,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "منشور",
    },
    {
      id: 3,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "منشور",
    },
    {
      id: 4,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "منشور",
    },
    {
      id: 5,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "مغلق",
    },
    {
      id: 6,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "منشور",
    },
    {
      id: 7,
      product: "001234",
      name: "ورد أحمد صناعي",
      category: "ورد زينة",
      codeP: "7888888",
      Qt: "80",
      price: "05",
      publish: "منشور",
    },
  ];

  const changeDisplay = (e) => {
    // getDisplayFormat();
    if (e.target.id === "bar") {
      getDisplayFormat("bar");
      getColorFormat(true);
    } else {
      getDisplayFormat("border");
      getColorFormat(false);
    }
  };

  return (
    <div className="products">
      <h1 className="title-dashboard">المنتجـات</h1>
      <p>عدد المنتجات 37 منتج</p>
      <div className="search-add-product flex-between">
        <div className="search">
          <input type="text" placeholder="البحث" />
          <button>بحث</button>
        </div>
        <button>اضافة منتج</button>
      </div>
      <div className="display-table flex">
        <span>شكل العرض</span>
        <i
          className={`fa-solid fa-bars bar ${colorFormat ? `specifie` : ``}`}
          onClick={changeDisplay}
          id="bar"
        ></i>
        <i
          className="fa-solid fa-border-all"
          onClick={changeDisplay}
          id="border"
        ></i>
      </div>
      {displayFormat === "bar" ? (
        <TableProduct itemsProducts={itemsProducts} />
      ) : (
        <ProductBox itemsProducts={itemsProducts} />
      )}
    </div>
  );
};

export default Products;
