      daysNumber = 30;
      break;
    case 9:
      monthString = "October";
      daysNumber = 31;
      break;
    case 10:
      monthString = "November";
      daysNumber = 30;
      break;
    case 11:
      monthString = "December";
      daysNumber = 31;
      break;
    default:
      break;
  }

  return (
    <div>
      <Calendar month={monthString} days={daysNumber} dayToday={20} />
    </div>
  )
}

