import { createSlice } from "@reduxjs/toolkit";


const addItemSlice = createSlice({
  name: "addItem",
  initialState: { showAdd: false },
  reducers: {
    getShowAdd: (state) => {
      state.name = true;
    }
  }
});

export const { getShowAdd } = addItemSlice.actions;

export default addItemSlice.reducer;
