import React from "react";
import { useState } from "react";
import Table from "../Table";
import ProductBox from "./ProductBox";

import "./products.css";
import Step from "./Step";

const Products = () => {
  const [displayFormat, getDisplayFormat] = useState("bar");
  const [showStep, getShowStep] = useState(false);

  const changeShowStep = () => {
    getShowStep(!showStep);
  };

  const headLines = [
    {
      id: 1,
      title: "المنتج",
    },
    {
      id: 2,
      title: "الاسم",
    },
    {
      id: 3,
      title: "رمز المنتج",
    },
    {
      id: 4,
      title: "التصنيف",
    },
    {
      id: 5,
      title: "الكمية",
    },
    {
      id: 6,
      title: "السعر",
    },
    {
      id: 7,
      title: "النشر على المتجر",
    },
    {
      id: 8,
      title: "اجراءات",
    },
  ];

  const items = [
    [
      {
        id: 1,
        title: "123456789",
      },
      {
        id: 2,
        title: "ورد أحمد صناعي ",
      },
      {
        id: 3,
        title: "7888888",
      },
      {
        id: 4,
        title: "ورد بيوت",
      },
      {
        id: 5,
        title: "100",
      },
      {
        id: 6,
        title: "11",
      },
      {
        id: 7,
        title: <span>منشور</span>,
        state: true,
      },
    ],
    [
      {
        id: 1,
        title: "123456789",
      },
      {
        id: 2,
        title: "ورد أحمد صناعي ",
      },
      {
        id: 3,
        title: "7888888",
      },
      {
        id: 4,
        title: "ورد بيوت",
      },
      {
        id: 5,
        title: "100",
      },
      {
        id: 6,
        title: "11",
      },
      {
        id: 7,
        title: <span>مغلق</span>,
        state: false,
      },
    ],
    [
      {
        id: 1,
        title: "123456789",
      },
      {
        id: 2,
        title: "ورد أحمد صناعي ",
      },
      {
        id: 3,
        title: "7888888",
      },
      {
        id: 4,
        title: "ورد بيوت",
      },
      {
        id: 5,
        title: "100",
      },
      {
        id: 6,
        title: "11",
      },
      {
        id: 7,
        title: <span>منشور</span>,
        state: true,
      },
    ],
  ];

  const item = [
    {
      id: 1,
      name: "aaa",
      age: "20",
      sex: "male",
    },
    {
      id: 2,
      name: "aaa",
      age: "20",
      sex: "male",
    },
  ];

  const result = item.map((el) => {
    return Object.values(el).map((e) => {
      return (
        <tr>
          <td>{e}</td>
        </tr>
      );
    });
  });

  const changeDisplay = () => {
    getDisplayFormat();
  };

  //   const resutlBox = bodyTitle.map((el) => {
  //   return <ProductBox key={el.id} box={el} headerTitle={headerTitle} />;
  // });

  return (
    <div className="products">
      <h1 className="title-dashboard">المنتجـات</h1>
      <p>عدد المنتجات 37 منتج</p>
      <div className="search-add-product flex-between">
        <div className="search">
          <input type="text" placeholder="البحث" />
          <button>بحث</button>
        </div>
        <button>اضافة منتج</button>
      </div>
      <div className="display-table flex">
        <span>شكل العرض</span>
        <i className="fa-solid fa-bars bar" onClick={changeDisplay}></i>
        <i className="fa-solid fa-border-all" onClick={changeDisplay}></i>
      </div>
      <Table
        headLines={headLines}
        items={items}
        publish={true}
        changeShowStep={changeShowStep}
        showStep={showStep}
      />
      <table>{result}</table>
      {/* <div className="grid-container">{resutlBox}</div> */}
    </div>
  );
};

export default Products;
