import React from "react";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import AddCategory from "./categories/AddCategory";

import "./customComp.css";
import ProductBox from "./products/ProductBox";

const CustomComp = ({
  title,
  paragraphe,
  add,
  table: Table,
  items,
  boxx,
  navigate,
  navigateTo,
  onClick,
  children,
  addCategory,
  addClient,
}) => {
  // const [displayFormat, getDisplayFormat] = useState("bar");
  // const [colorFormat, getColorFormat] = useState(true);

  // const changeDisplay = (e) => {
  //   if (e.target.id === "bar") {
  //     getDisplayFormat("bar");
  //     getColorFormat(true);
  //   } else {
  //     getDisplayFormat("border");
  //     getColorFormat(false);
  //   }
  // };

  const displayFormat = useSelector((state) => state.products.displayFormat);

  return (
    <div className="custom-comp">
      <h1 className="title-dashboard">{title}</h1>
      <p>{paragraphe}</p>
      <div className="search-add-product flex-between">
        <div className="search">
          <input type="text" placeholder="البحث" />
          <button onClick={onClick}>بحث</button>
        </div>
        {navigate ? (
          <NavLink to={navigateTo}>{add}</NavLink>
        ) : (
          <button onClick={onClick}>{add}</button>
        )}
      </div>

      {children}

      {boxx ? (
        displayFormat === "bar" ? (
          <Table items={items} />
        ) : (
          <ProductBox itemsProducts={items} />
        )
      ) : (
        <Table items={items} />
      )}

      {addCategory && <AddCategory />}

      {addClient && <AddCustomer />}
    </div>
  );
};

export default CustomComp;
