import React, { useState } from 'react'

import img from '../images/products.jpg'
import Step from './Step'

const ProductBox = ({ box, headerTitle }) => {


  const [show, getShow] = useState(false);

  const showDisplay = () => {
    getShow(!show);
  }
  const result = headerTitle.map((el) => {
    return box.map((b) => {
      if (el.id !== 1 && el.id !== 7 && el.id !== 8) {
        if (el.id === b.id) {
          return <div key={el.id}>
            <span>{el.title} :</span>
            <p>{b.title}</p>
          </div>
        }
      }
      return null;
    })
  })

  return (
    <div className='products-box'>
      <div className='photo'>
        <img src={img} alt="product" />
        <div className='flex-between'>
          <span>منشور</span>
          <div onClick={showDisplay}>
            <i className="fa-solid fa-ellipsis-vertical"></i>
            {
              show && <Step />
            }
          </div>
        </div>
      </div>
      <div className='info-product flex'>
        {result}
      </div>
    </div>
  )
}

export default ProductBox
