import React, { useState } from 'react'
import Plan from './Plan'

import './plans.css';


const Plans = () => {

  const [pricePlan, getPricePlan] = useState("month");

  const plans = [
    {
      id: 1,
      title: 'ميني',
      priceMonth: "مجانا",
      priceYear: "مجانا",
      txt: 'هذا النص هو مثال لنص يمكن أن يستبدل في نفس المساحة, لقد تم توليد هذا النص',
      features: ['نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل']
    },
    {
      id: 2,
      title: 'برو',
      priceMonth: 39.99,
      priceYear: "مجانا",
      txt: 'هذا النص هو مثال لنص يمكن أن يستبدل في نفس المساحة, لقد تم توليد هذا النص',
      features: ['نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل']
    },
    {
      id: 3,
      title: 'بزنس',
      priceMonth: 149.99,
      priceYear: "مجانا",
      txt: 'هذا النص هو مثال لنص يمكن أن يستبدل في نفس المساحة, لقد تم توليد هذا النص',
      features: ['نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل', 'نص يمكن أن يستبدل'],
    },
  ]
  const result = plans.map((plan) => {

    return <Plan
      key={plan.id}
      title={plan.title}
      price={plan.price}
      txt={plan.txt}
      features={plan.features}
      package={pricePlan === "month" ? "شهر" : "سنة"}
    />
  })

  const yearly = () => {
    return getPricePlan("year")
  }

  const monthly = () => {
    return getPricePlan("month")
  }


  return (
    <div className='plans-comp'>
      <h1 className='title-head'>باقات تجربة</h1>
      <div>
        <span className='yearly' onClick={yearly}>سنوي</span>
        <span className='monthly' onClick={monthly}>شهري</span>
      </div>

      <div className='grid-container plans'>
        {result}
      </div>
    </div>
  )
}

export default Plans
