import { createSlice } from "@reduxjs/toolkit";


const productSlice = createSlice({
  name: "products",
  initialState: { displayFormat: "border", colorFormat: true },
  reducers: {
    changeDisplay: (state, action) => {
      // if (e.target.id === "bar") {
      //   state.displayFormat = "bar";
      //   state.colorFormat = true;
      // } else {
      //   state.displayFormat = "border";
      //   state.colorFormat = false;
      // }
      console.log(state);
      console.log(state.displayFormat);
      console.log(state.colorFormat);
      console.log(action);
      console.log(action.payload);
    }
  }
});

export const { changeDisplay } = productSlice.actions;

export default productSlice.reducer;
