import { createSlice } from "@reduxjs/toolkit";


const addItemSlice = createSlice({
  name: "addItem",
  initialState: { addCategory: false, addClient: false },
  reducers: {
    getaddCategory: (state) => {
      state.addCategory = true;
    },
    getaddClient: (state) => {
      state.addClient = true;
    }
  }
});

export const { getaddCategory, getaddClient } = addItemSlice.actions;

export default addItemSlice.reducer;
