import { createSlice } from "@reduxjs/toolkit";


const infoShopSlice = createSlice({
  name: "infoShop",
  initialState: { name: "" },
  reducers: {
    putName: (state, action) => {
      state.name = action;
    }
  }
});

export const { putName } = infoShopSlice.actions;

export default infoShopSlice.reducer;
