import React, { Fragment, useState } from 'react'

const Form = ({ items, submitValue, onSubmit }) => {

  const [inputVal, getInputVal] = useState("");

  const result = items.map(item => {

    return (
      <Fragment key={item.id}>
        <label>{item.labelName}</label>
        <input
          type={item.type}
          placeholder={`ادخل ${item.labelName}`}
          value={inputVal}
          onChange={(e) => getInputVal(e.target.value)}
          required />
      </Fragment>
    )
  });

  return (
    <form className='form-shop' onSubmit={onSubmit}>
      {result}
      <input type='submit' value={submitValue} />
    </form>
  )
}

export default Form
