import React, { Fragment } from 'react'

const Form = ({ items, submitValue, onSubmit }) => {

  const result = items.map(item => {
    return (
      <Fragment key={item.id}>
        <label>{item.labelName}</label>
        <input type={item.type} placeholder={`ادخل ${item.labelName}`} required />

      </Fragment>
    )
  });

  return (
    <form className='form-shop' onSubmit={onSubmit}>
      {result}
      <input type='submit' value={submitValue} />
    </form>
  )
}

export default Form
