import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import Form from "./Form";

const CreateShop = () => {
  const [verify, getVerify] = useState(false);

  const items1 = [
    {
      id: 1,
      labelName: "الاسم كاملاً",
      type: "text",
    },
    {
      id: 2,
      labelName: "البريد الإلكتروني",
      type: "email",
    },
    {
      id: 3,
      labelName: "رقم الهاتف",
      type: "phone",
    },
    {
      id: 4,
      labelName: "كلمة المرور",
      type: "password",
    },
  ];

  const items2 = [
    {
      id: 1,
      labelName: "اسم المتجر بالعربية",
      type: "text",
    },
    {
      id: 2,
      labelName: "اسم المتجر بالانجليزية",
      type: "text",
    },
    {
      id: 3,
      labelName: "رابط المتجر الخاص بك سيكون كالتالي",
      type: "text",
    },
    {
      id: 4,
      labelName: "كيف سمعت عن المنصة",
      type: "text",
    },
  ];

  const submitValue = "التالي";

  const nextHandler1 = (e) => {
    e.preventDefault();
    console.log("Next1");
  };

  const nextHandler2 = (e) => {
    e.preventDefault();
    console.log("Next2");
  };

  return (
    <div className="create-shop">
      <h1 className="title-store">أنشئ متجرك بخطوتين</h1>
      <Form items={items1} submitValue={submitValue} onSubmit={nextHandler1} />
      <p>
        هل لديك حساب في تجربة <NavLink to="/">تسجيل الدخول</NavLink>
      </p>

      {verify && (
        <>
          <Form
            items={items2}
            submitValue={submitValue}
            onSubmit={nextHandler2}
          />
          <p>
            مجرد إنشاء الحساب أنت توافق عن <a href="">الشروط و الأحكام</a> و{" "}
            <a href="">سياسة الخصوصية</a>
          </p>
        </>
      )}
    </div>
  );
};

export default CreateShop;
